package Loops;

public class ForLoop {
	int i,j;
	public void rundemo()
	{
		for(i=10,j=0;i>0;i--)
		{
			System.out.println(i);
			System.out.println(" j is"+j);
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		ForLoop oo=new ForLoop();
		oo.rundemo();
	}

}
